import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

// Configurar axios para usar el backend en puerto 8003
const API_BASE_URL = 'http://localhost:8003';
axios.defaults.baseURL = API_BASE_URL;

function App() {
  const [duration, setDuration] = useState(20);
  const [connectionType, setConnectionType] = useState('wifi');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [progress, setProgress] = useState({ message: '', percentage: 0 });
  const [results, setResults] = useState(null);
  const [alert, setAlert] = useState(null);
  const [databaseStatus, setDatabaseStatus] = useState(null);

  // Verificar estado de la base de datos al cargar
  useEffect(() => {
    checkDatabaseStatus();
  }, []);

  const checkDatabaseStatus = async () => {
    try {
      const response = await axios.get('/database/status');
      setDatabaseStatus(response.data);
    } catch (error) {
      console.error('Error verificando base de datos:', error);
    }
  };

  const showAlert = (message, type) => {
    setAlert({ message, type });
    setTimeout(() => setAlert(null), 5000);
  };

  const updateProgress = (message, percentage) => {
    setProgress({ message, percentage });
  };

  const startAnalysis = async () => {
    if (!duration || !connectionType) {
      showAlert('Por favor, complete todos los campos.', 'danger');
      return;
    }

    setIsAnalyzing(true);
    setResults(null);
    updateProgress('1. Preparando captura de tráfico...', 20);

    try {
      const response = await axios.post('/analyze', {
        duration: parseInt(duration),
        connection_type: connectionType
      });

      if (response.data.success) {
        updateProgress('✓ Análisis completado con éxito', 100);
        setTimeout(() => {
          setResults(response.data);
          setIsAnalyzing(false);
          showAlert(
            `Análisis completado: ${response.data.summary.total_flows} flujos procesados con ${response.data.summary.columns_count} características`,
            'success'
          );
          // Actualizar estado de la base de datos
          checkDatabaseStatus();
        }, 1000);
      } else {
        throw new Error(response.data.error || 'Error desconocido en el análisis');
      }
    } catch (error) {
      console.error('Error:', error);
      showAlert(
        `❌ Error en el análisis: ${error.response?.data?.detail || error.message}`,
        'danger'
      );
      setIsAnalyzing(false);
    }
  };

  const getSummary = (predictions) => {
    return predictions.reduce((acc, pred) => {
      if (pred.label === 'BENIGN') {
        acc.benign++;
      } else if (['Bot', 'DDoS', 'DoS', 'BruteForce', 'Infiltration'].includes(pred.label)) {
        acc.malicious++;
      } else {
        acc.suspicious++;
      }
      return acc;
    }, { benign: 0, suspicious: 0, malicious: 0 });
  };

  const getBadgeClass = (label) => {
    if (label === 'BENIGN') return 'badge-benign';
    if (['Bot', 'DDoS', 'DoS', 'BruteForce', 'Infiltration'].includes(label)) return 'badge-malicious';
    return 'badge-suspicious';
  };

  const getBackgroundColor = (label) => {
    if (label === 'BENIGN') return 'rgba(16, 185, 129, 0.1)';
    if (['Bot', 'DDoS', 'DoS', 'BruteForce', 'Infiltration'].includes(label)) return 'rgba(239, 68, 68, 0.1)';
    return 'rgba(245, 158, 11, 0.1)';
  };

  return (
    <div className="App">
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg navbar-dark">
        <div className="container">
          <a className="navbar-brand" href="#">
            <i className="fas fa-shield-alt me-2"></i>
            ML Traffic Analyzer
          </a>
          <span className="navbar-text">
            <i className="fas fa-brain me-1"></i>
            Análisis Inteligente de Tráfico de Red
          </span>
        </div>
      </nav>

      <div className="container my-4">
        {/* Alert */}
        {alert && (
          <div className={`alert alert-${alert.type} alert-dismissible fade show`} role="alert">
            <i className={`fas fa-${alert.type === 'success' ? 'check-circle' : alert.type === 'danger' ? 'exclamation-circle' : 'info-circle'} me-2`}></i>
            {alert.message}
            <button
              type="button"
              className="btn-close"
              onClick={() => setAlert(null)}
            ></button>
          </div>
        )}

        {/* Formulario de Configuración */}
        <div className="row mb-4">
          <div className="col-lg-8 mx-auto">
            <div className="card">
              <div className="card-header">
                <h4 className="card-title mb-0">
                  <i className="fas fa-cog me-2"></i>
                  Configuración del Análisis
                </h4>
              </div>
              <div className="card-body">
                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label htmlFor="duration" className="form-label">
                      <i className="fas fa-clock me-2"></i>
                      Duración de Captura: {duration} segundos
                    </label>
                    <input
                      type="range"
                      className="form-range"
                      id="duration"
                      min="5"
                      max="60"
                      value={duration}
                      onChange={(e) => setDuration(e.target.value)}
                    />
                    <div className="d-flex justify-content-between">
                      <small className="text-muted">5s</small>
                      <small className="text-muted">60s</small>
                    </div>
                  </div>
                  <div className="col-md-6 mb-3">
                    <label htmlFor="connectionType" className="form-label">
                      <i className="fas fa-wifi me-2"></i>
                      Tipo de Conexión
                    </label>
                    <select
                      className="form-select"
                      id="connectionType"
                      value={connectionType}
                      onChange={(e) => setConnectionType(e.target.value)}
                    >
                      <option value="wifi">WiFi</option>
                      <option value="ethernet">Ethernet</option>
                      <option value="mobile">Móvil</option>
                    </select>
                  </div>
                </div>
                <div className="text-center">
                  <button
                    className="btn btn-primary btn-lg"
                    onClick={startAnalysis}
                    disabled={isAnalyzing}
                  >
                    {isAnalyzing ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                        Analizando...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-play me-2"></i>
                        Iniciar Análisis
                      </>
                    )}
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Estado de la Base de Datos */}
        {databaseStatus && (
          <div className="row mb-4">
            <div className="col-lg-8 mx-auto">
              <div className="card">
                <div className="card-header">
                  <h5 className="card-title mb-0">
                    <i className="fas fa-database me-2"></i>
                    Estado de la Base de Datos
                  </h5>
                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-md-4">
                      <div className="text-center">
                        <div className={`icon-container ${databaseStatus.status === 'connected' ? 'icon-success' : 'bg-danger'} mx-auto`}>
                          <i className={`fas ${databaseStatus.status === 'connected' ? 'fa-check' : 'fa-times'} fa-lg`}></i>
                        </div>
                        <h6>{databaseStatus.status === 'connected' ? 'Conectado' : 'Error'}</h6>
                        <small className="text-muted">{databaseStatus.database}</small>
                      </div>
                    </div>
                    <div className="col-md-4">
                      <div className="text-center">
                        <h3 className="text-primary">{databaseStatus.total_records || 0}</h3>
                        <p className="text-muted">Registros Totales</p>
                      </div>
                    </div>
                    <div className="col-md-4">
                      <div className="text-center">
                        <h6>Host: {databaseStatus.host}:{databaseStatus.port}</h6>
                        <small className="text-muted">PostgreSQL</small>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Progreso */}
        {isAnalyzing && (
          <div className="row mb-4">
            <div className="col-12">
              <div className="card">
                <div className="card-body">
                  <div className="text-center">
                    <div className="spinner-border mb-3" role="status">
                      <span className="visually-hidden">Analizando...</span>
                    </div>
                    <h5>Analizando Tráfico de Red</h5>
                    <p className="text-primary">{progress.message}</p>
                    <div className="progress mb-3">
                      <div
                        className="progress-bar progress-bar-striped progress-bar-animated"
                        role="progressbar"
                        style={{ width: `${progress.percentage}%` }}
                      ></div>
                    </div>
                    <small className="text-muted">Progreso: {progress.percentage}%</small>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Resultados */}
        {results && (
          <div className="row">
            <div className="col-12">
              <div className="card">
                <div className="card-header">
                  <h4 className="card-title mb-0">
                    <i className="fas fa-chart-line me-2"></i>
                    Resultados del Análisis
                  </h4>
                </div>
                <div className="card-body">
                  {(() => {
                    const summary = getSummary(results.predictions);
                    return (
                      <div className="row mb-4">
                        <div className="col-md-4">
                          <div className="text-center">
                            <div className="icon-container icon-success mx-auto">
                              <i className="fas fa-shield-check fa-lg"></i>
                            </div>
                            <h3 className="text-success">{summary.benign}</h3>
                            <p className="text-muted">Conexiones Seguras</p>
                          </div>
                        </div>
                        <div className="col-md-4">
                          <div className="text-center">
                            <div
                              className="icon-container mx-auto"
                              style={{
                                background: 'linear-gradient(135deg, var(--warning-color) 0%, #d97706 100%)',
                                color: 'white'
                              }}
                            >
                              <i className="fas fa-exclamation-triangle fa-lg"></i>
                            </div>
                            <h3 className="text-warning">{summary.suspicious}</h3>
                            <p className="text-muted">Conexiones Sospechosas</p>
                          </div>
                        </div>
                        <div className="col-md-4">
                          <div className="text-center">
                            <div
                              className="icon-container mx-auto"
                              style={{
                                background: 'linear-gradient(135deg, var(--danger-color) 0%, #dc2626 100%)',
                                color: 'white'
                              }}
                            >
                              <i className="fas fa-shield-virus fa-lg"></i>
                            </div>
                            <h3 className="text-danger">{summary.malicious}</h3>
                            <p className="text-muted">Amenazas Detectadas</p>
                          </div>
                        </div>
                      </div>
                    );
                  })()}

                  <h5 className="mb-3">Detalle de Predicciones:</h5>
                  <div className="row">
                    {results.predictions.map((pred, index) => (
                      <div key={index} className="col-md-6 col-lg-4 mb-3">
                        <div
                          className="card border-0"
                          style={{ background: getBackgroundColor(pred.label) }}
                        >
                          <div className="card-body">
                            <div className="d-flex justify-content-between align-items-center">
                              <div>
                                <h6 className="card-title">Flujo #{index + 1}</h6>
                                <span className={`badge ${getBadgeClass(pred.label)}`}>
                                  {pred.label}
                                </span>
                              </div>
                              <div className="text-end">
                                <h5 className="mb-0">{(pred.confidence * 100).toFixed(1)}%</h5>
                                <small className="text-muted">Confianza</small>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Tabla de datos completos */}
                  {results.full_data && results.full_data.length > 0 && (
                    <div className="mt-4">
                      <h5 className="mb-3">
                        <i className="fas fa-table me-2"></i>
                        Datos Completos ({results.summary.columns_count} Columnas)
                      </h5>
                      <div className="results-container">
                        <div className="table-responsive">
                          <table className="table table-striped">
                            <thead>
                              <tr>
                                <th>#</th>
                                <th>Predicción</th>
                                <th>Confianza</th>
                                <th>Puerto Destino</th>
                                <th>Duración Flujo</th>
                                <th>Paquetes Fwd</th>
                                <th>Paquetes Bwd</th>
                                <th>Bytes/s</th>
                                <th>Acción</th>
                              </tr>
                            </thead>
                            <tbody>
                              {results.full_data.slice(0, 10).map((row, index) => (
                                <tr key={index}>
                                  <td>{index + 1}</td>
                                  <td>
                                    <span className={`badge ${getBadgeClass(row.Prediction)}`}>
                                      {row.Prediction}
                                    </span>
                                  </td>
                                  <td>{(row.Confidence * 100).toFixed(1)}%</td>
                                  <td>{row['Destination Port']?.toFixed(0) || 'N/A'}</td>
                                  <td>{row['Flow Duration']?.toFixed(2) || 'N/A'}</td>
                                  <td>{row['Total Fwd Packets']?.toFixed(0) || 'N/A'}</td>
                                  <td>{row['Total Backward Packets']?.toFixed(0) || 'N/A'}</td>
                                  <td>{row['Flow Bytes/s']?.toFixed(2) || 'N/A'}</td>
                                  <td>
                                    <button className="btn btn-sm btn-outline-primary">
                                      <i className="fas fa-eye"></i>
                                    </button>
                                  </td>
                                </tr>
                              ))}
                            </tbody>
                          </table>
                        </div>
                        {results.full_data.length > 10 && (
                          <div className="text-center mt-3">
                            <small className="text-muted">
                              Mostrando 10 de {results.full_data.length} registros
                            </small>
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
